import React from "react";
import "./Product.css";
import { useStateValue } from "./StateProvider";

function Product({ id, title, image, price, rating, delivery, ubicacion }) {
  const [{ basket }, dispatch] = useStateValue();

  const addToBasket = () => {
    // dispatch the item into the data layer
    dispatch({
      type: "ADD_TO_BASKET",
      item: {
        id: id,
        title: title,
        image: image,
        price: price,
        rating: rating,
        delivery: delivery,
        ubicacion: ubicacion,
      },
    });
  };

  return (
    <div className="product">
      <div className="product__info">
        <p>{title}</p>
        <p className="product__price">
          <small>S/.</small>
          <strong>{price}</strong>
        </p>
        <strong>{ubicacion}</strong>
        <br></br>

        <p>Delivery: {delivery}</p>
        <div className="product__rating">
          {Array(rating)
            .fill()
